$(document).ready(function(){var t=400,e=300;$(window).scroll(function(){jQuery(this).scrollTop()>t?jQuery(".back_to_top").fadeIn(e):jQuery(".back_to_top").fadeOut(e)}),$(".back_to_top").click(function(t){return t.preventDefault(),$("html, body").animate({scrollTop:0},e),!1}),$('a[href*="#"]:not([href="#"])').click(function(){if(location.pathname.replace(/^\//,"")===this.pathname.replace(/^\//,"")&&location.hostname===this.hostname){var t=$(this.hash);if(t=t.length?t:$("[name="+this.hash.slice(1)+"]"),t.length)return $("html, body").animate({scrollTop:t.offset().top},1e3),!1}}),$("#mblmenu").click(function(){$(".mblnav").slideToggle()})});